import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { tasksAPI } from '../../services/api';
import { useAuth } from '../../contexts/AuthContext';
import { format, parseISO, isAfter, isToday } from 'date-fns';
import {
    Plus,
    Filter,
    Search,
    Calendar,
    User,
    AlertCircle,
    CheckCircle,
    Clock,
    X,
    Edit,
    Trash2,
    Eye
} from 'lucide-react';
import toast from 'react-hot-toast';

const Tasks = () => {
    const { user } = useAuth();
    const queryClient = useQueryClient();
    const [showCreateModal, setShowCreateModal] = useState(false);
    const [showEditModal, setShowEditModal] = useState(false);
    const [selectedTask, setSelectedTask] = useState(null);
    const [filters, setFilters] = useState({
        status: '',
        priority: '',
        search: ''
    });

    // Get tasks
    const { data: tasksData, isLoading } = useQuery(
        ['tasks', filters],
        () => tasksAPI.getTasks(filters), {
            refetchInterval: 30000,
        }
    );

    // Get task stats
    const { data: taskStats } = useQuery(
        'task-stats',
        () => tasksAPI.getTaskStats(), {
            enabled: user ?.role === 'employee'
        }
    );

    // Update task mutation
    const updateTaskMutation = useMutation(
        ({ id, data }) => tasksAPI.updateTask(id, data), {
            onSuccess: () => {
                queryClient.invalidateQueries('tasks');
                queryClient.invalidateQueries('task-stats');
                toast.success('Task updated successfully!');
            },
            onError: (error) => {
                toast.error(error.response ?.data ?.message || 'Update failed');
            }
        }
    );

    // Delete task mutation
    const deleteTaskMutation = useMutation(
        (id) => tasksAPI.deleteTask(id), {
            onSuccess: () => {
                queryClient.invalidateQueries('tasks');
                toast.success('Task deleted successfully!');
            },
            onError: (error) => {
                toast.error(error.response ?.data ?.message || 'Delete failed');
            }
        }
    );

    const handleStatusChange = (taskId, newStatus) => {
        updateTaskMutation.mutate({
            id: taskId,
            data: { status: newStatus }
        });
    };

    const handleDelete = (taskId) => {
        if (window.confirm('Are you sure you want to delete this task?')) {
            deleteTaskMutation.mutate(taskId);
        }
    };

    const getPriorityColor = (priority) => {
        const colors = {
            low: 'bg-blue-100 text-blue-800',
            medium: 'bg-yellow-100 text-yellow-800',
            high: 'bg-orange-100 text-orange-800',
            urgent: 'bg-red-100 text-red-800'
        };
        return colors[priority] || colors.medium;
    };

    const getStatusColor = (status) => {
        const colors = {
            pending: 'bg-gray-100 text-gray-800',
            in_progress: 'bg-blue-100 text-blue-800',
            completed: 'bg-green-100 text-green-800',
            cancelled: 'bg-red-100 text-red-800'
        };
        return colors[status] || colors.pending;
    };

    const isOverdue = (dueDate) => {
        if (!dueDate) return false;
        return isAfter(new Date(), parseISO(dueDate)) && !isToday(parseISO(dueDate));
    };

    const tasks = tasksData ?.data ?.tasks || [];

    return ( <
            div className = "space-y-6" > { /* Header */ } <
            div className = "flex justify-between items-center" >
